function danw_settings_handler(){
	
	$danw_action;
	if(isset($_POST['danw_action'])){
		$danw_action =  sanitize_text_field($_POST['danw_action']);
	}
	
	if($danw_action && $danw_action === 'true') {
	
		$iSecondsFirst = intval(sanitize_text_field($_POST['danw_first_time_notifcation']));
		if($iSecondsFirst < 5){
			$iSecondsFirst = 5;
		}
    
		$iSeconds = intval(sanitize_text_field($_POST['danw_time_notifcation']));
		if($iSeconds < 20){
			$iSeconds = 20;
		}
    
		//$iSecondsCustom = intval(sanitize_text_field($_POST['danw_time_custom_notifcation']));
		//if($iSecondsCustom < 20){
		//	$iSecondsCustom = 20;
		//}
    
		$iDaysAgo = intval(sanitize_text_field($_POST['danw_day_notification']));
		if($iDaysAgo < 1){
			$iDaysAgo = 1;
		}
    
		$iDuration = intval(sanitize_text_field($_POST['danw_duration_notification']));
		if($iDuration < 4){
			$iDuration = 4;
		}
		if($iDuration > $iSeconds){
			$iDuration = round($iSeconds / 2);
		}
	
 

		$iDurationToMs = $iDuration * 1000; 
		$iSecondToMs = $iSeconds * 1000; 
		$iSecondsFirstToMs = $iSecondsFirst * 1000;
		//$iSecondCustomToMs = $iSecondsCustom * 1000;
		$textNotificationView = strip_tags(str_replace('"', "'", sanitize_text_field($_POST['danw_text_notifcation_view'])));
		$showPoweredBy = sanitize_text_field($_POST['danw_show_powered_by']);
		
		update_option('danw_first_time_notifcation', $iSecondsFirstToMs);
		update_option('danw_time_notifcation', $iSecondToMs);
		update_option('danw_day_notification', $iDaysAgo);
		update_option('danw_duration_notification', $iDurationToMs);
		update_option('danw_text_notifcation_view', $textNotificationView);
		update_option('danw_show_powered_by', $showPoweredBy);
		if(isset($_POST['danw_display_notification'])){
			$displayNotification = sanitize_text_field($_POST['danw_display_notification']);
			update_option('danw_display_notification', $displayNotification);
		}
		if(isset($_POST['danw_viewed_post'])){
			$viewedPost = intval(sanitize_text_field($_POST['danw_viewed_post']));
			update_option('danw_viewed_post', $viewedPost);
		} else {
			update_option('danw_viewed_post', 0);
		}
		if(isset($_POST['danw_viewed_page'])){
			$viewedPage = intval(sanitize_text_field($_POST['danw_viewed_page']));
			update_option('danw_viewed_page', $viewedPage);
		} else {
			update_option('danw_viewed_page', 0);
		}
		$bOptionsSaved = true;
	
	}
	
	global $wpdb;
	$aNotifications = $wpdb->get_results("SELECT a.post_id, a.meta_value, a.meta_key FROM $wpdb->postmeta AS a WHERE (a.meta_key = 'danw_time_viewed_page' || a.meta_key = 'danw_time_ordered_product' || a.meta_key = 'danw_time_viewed_post' || a.meta_key = 'danw_time_viewed_product') ORDER BY a.meta_value DESC");
	
	$aNotifcationData = array();
	$iCounter = 0;
	foreach($aNotifications as $oPost){
		$aNotifcationData[$iCounter]['count'] = danw_getNotificationCount($oPost->post_id, $oPost->meta_key);
		$aNotifcationData[$iCounter]['meta_value'] = $oPost->meta_value;
		$aNotifcationData[$iCounter]['meta_key'] = $oPost->meta_key;
		$aNotifcationData[$iCounter]['post_id'] = $oPost->post_id;
		$iCounter++;
	}
	
	danw_get_settings_page($aNotifcationData);
}
 
