function danw_getNotificationCount($iPostID, $sType){
    if(empty($iPostID) || empty($sType)){
        return false;
    }
    
    $sTypeArg = '';
    switch ($sType){
       case 'danw_time_viewed_page':
           $sTypeArg = 'danw_count_viewed_page';
           break;
       default :
           $sTypeArg = 'danw_count_viewed_post';
           break;
    }
    
    global $wpdb;
	$iPostIdPrepared = intval($iPostID);
    $iCount = $wpdb->get_results($wpdb->prepare("SELECT a.meta_value FROM $wpdb->postmeta AS a WHERE a.post_id = %d AND a.meta_key = %s LIMIT 1", $iPostIdPrepared, $sTypeArg));
    if(isset($iCount[0])){
        return $iCount[0]->meta_value;
    }
    return false;
}
